INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (1, 'Pong', 'Sports', 'Atari', 'Arcade', 1972, 'Pong is a two-dimensional sports game that simulates table tennis. The player controls an in-game paddle by moving it vertically across the left or right side of the screen. They can compete against another player controlling a second paddle on the opposing side.', 'https://upload.wikimedia.org/wikipedia/commons/6/62/Pong_Game_Test2.gif');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (2, 'Zork', 'Adventure', 'Infocom', 'PDP-10', 1977, 'Zork is set in \"the ruins of an ancient empire lying far underground\". The player is a nameless adventurer \"who is venturing into this dangerous land in search of wealth and adventure\". The goal is to return from exploring the \"Great Underground Empire\" (GUE, for short) alive and with all treasures needed to complete each adventure,ultimately inheriting the title of Dungeon Master.', 'https://upload.wikimedia.org/wikipedia/en/a/ac/Zork_I_box_art.jpg');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (3, 'Space Invaders', 'Shoot \'em up', 'Taito', 'Arcade', 1978, 'Space Invaders is a fixed shooter in which the player controls a laser cannon by moving it horizontally across the bottom of the screen and firing at descending aliens. ', 'https://upload.wikimedia.org/wikipedia/en/2/20/SpaceInvaders-Gameplay.gif');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (4, 'Asteroids', 'Shoot \'em up', 'Atari', 'Arcade', 1979, 'The objective of Asteroids is to destroy asteroids and saucers. The player controls a triangular ship that can rotate left and right, fire shots straight forward, and thrust forward.', 'https://upload.wikimedia.org/wikipedia/en/8/81/Asteroids-arcadegame.jpg');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (5, 'Adventure', 'Action-adventure', 'Atari', 'Atari 2600', 1980, 'In Adventure, the player\'s goal is to recover the Enchanted Chalice that an evil magician has stolen and hidden in the kingdom and return it to the Golden Castle.', 'https://upload.wikimedia.org/wikipedia/en/6/60/Adventure_Box_Front.jpg');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (6, 'Missile Command', 'Shoot \'em up', 'Atari', 'Arcade', 1980, 'The game is played by moving a crosshair across the sky background via a trackball and pressing one of three buttons to launch a counter-missile from the appropriate battery.', 'https://upload.wikimedia.org/wikipedia/en/c/c7/Missile_Command_flyer.jpg');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (7, 'Pac-Man', 'Maze', 'Namco', 'Arcade', 1980, 'Pac-Man is a maze chase video game; the player controls the eponymous character through an enclosed maze. The objective of the game is to eat all of the dots placed in the maze while avoiding four colored ghosts that pursue him.', 'https://upload.wikimedia.org/wikipedia/en/5/59/Pac-man.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (8, 'Centipede', 'Shoot \'em up', 'Atari', 'Arcade', 1981, 'In the original arcade version: The player\'s fighter is represented by a small insect-like head at the bottom of the screen called the Bug Blaster.', 'https://upload.wikimedia.org/wikipedia/en/6/6b/Centipede-arcade-flyer.jpg');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (9, 'Defender', 'Shoot \'em up', 'Williams Entertainment', 'Arcade', 1981, 'Defender is a two-dimensional side-scrolling shooting game set on the surface of an unnamed planet (or, in the Atari VCS version, an unnamed city). ', 'https://upload.wikimedia.org/wikipedia/en/e/ea/Defender_red_label.flyer.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (10, 'Donkey Kong', 'Platform', 'Nintendo', 'Arcade', 1981, 'As the first platform game to feature jumping, Donkey Kong requires the player to jump between gaps and over obstacles or approaching enemies, setting the template for the future of the platform genre.', 'https://upload.wikimedia.org/wikipedia/en/f/fd/Donkey_Kong_Gameplay.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (11, 'Frogger', 'Action', 'Sega', 'Arcade', 1981, 'The objective of the game is to guide a frog to each of the empty \"frog homes\" at the top of the screen.', 'https://upload.wikimedia.org/wikipedia/en/c/cd/Frogger_game_arcade.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (12, 'Galaga', 'Shoot \'em up', 'Namco', 'Arcade', 1981, 'Galaga is a fixed shooter video game. Its plot involves the launch of a starship named the Blast Fighter to destroy the hostile Galaga forces and their home planet.', 'https://upload.wikimedia.org/wikipedia/en/9/9b/Galag88j.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (13, 'Tempest', 'Shoot \'em up', 'Atari', 'Arcade', 1981, 'The goal in Tempest is to survive for as long as possible, and score as many points as possible, by clearing the screen of enemies that appear on the playing field.', 'https://upload.wikimedia.org/wikipedia/en/5/58/Tempest_arcade.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (14, 'Joust', 'Action', 'Williams Entertainment', 'Arcade', 1982, 'The player controls a yellow knight riding a flying ostrich. The player navigates the protagonist around the game world, which consists of rock platforms floating above a flat island surrounded by lava, via two-way joystick and a button. ', 'https://upload.wikimedia.org/wikipedia/en/f/f2/Joustarcadegame.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (15, 'Ms. Pac-Man', 'Maze', 'Midway', 'Arcade', 1982, 'The gameplay is very similar to that of Pac-Man. The player earns points by eating pellets and avoiding monsters (contact with one causes Ms. Pac-Man to lose a life). ', 'https://upload.wikimedia.org/wikipedia/en/4/42/Ms._Pac-Man_flyer.png');
INSERT INTO `game` (`id`, `name`, `genre`, `publisher`, `original_platform`, `released_year`, `description`, `image`) VALUES (16, 'Pitfall!', 'Platform', 'Activision', 'Atari 2600', 1982, 'The player controls Pitfall Harry through 256 horizontally connected screens of jungle in an attempt to recover 32 treasures in a 20-minute time period.', 'https://upload.wikimedia.org/wikipedia/en/0/06/Pitfall%21_Coverart.png');
